precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform ARGS{
  vec2 center;
  vec2 canvas_size;
  float wave_radius;
  float wave_offset;
  float wh_ratio;
};
void main () {
  vec2 st = gl_FragCoord.xy / canvas_size.xy;
  float scaleWH = canvas_size.x/canvas_size.y;
  vec2 distance_vec = st - center;
  float rx = abs(distance_vec.x);
  float ry = abs(distance_vec.y);
  float distance = length(vec2(rx * wh_ratio, ry / scaleWH * wh_ratio));
  float sin_factor = sin(distance * 100.0 + cc_time.x) * 0.05;
  float discard_factor = clamp(wave_radius - abs(wave_offset - distance), 0.0, 1.0);
  vec2 offset = normalize(distance_vec) * sin_factor * discard_factor;
  vec2 uv = offset + v_uv0;
  gl_FragColor = texture(texture, uv);
}

