glsl3.frag:
#if CC_SUPPORT_standard_derivatives
  #extension GL_OES_standard_derivatives : enable
#endif
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
in float v_dist;
uniform Props {
  vec2 screen;
  vec2 lightPos;
  float maxRadius;
  float whRatio;
};
void main () {
  vec4 o = v_color;
  vec2 st = gl_FragCoord.xy / screen.xy;
  float scaleWH = screen.x/screen.y;
  ALPHA_TEST(o);
  vec2 uv = st - lightPos;
  float rx = abs(uv.x);
  float ry = abs(uv.y);
  float fade = o.a;
  float radius = length(vec2(rx * whRatio, ry / scaleWH * whRatio));
  float radiusX = length(vec2(rx * whRatio, 0.0));
  float radiusY = length(vec2(0.0, ry / scaleWH * whRatio));
  float radiusXy = radiusX;
  if(radiusX<radiusY){
    radiusXy = radiusY;
  }
  o.a = 1.0 - smoothstep(0.05, maxRadius, radiusXy);
  o.a *= fade;
  #if CC_SUPPORT_standard_derivatives
    float aa = fwidth(v_dist);
  #else
    float aa = 0.05;
  #endif
  float alpha = 1. - smoothstep(-aa, 0., abs(v_dist) - 1.0);
  o.rgb *= o.a;
  o *= alpha;
  gl_FragColor = o;
}

