glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Properties {
  vec4 lightColor;
  vec2 size;
};
const float RADIUS = 20.0;
float getA(vec2 pos){
  vec4 color = texture2D(texture, pos);
  return 1.0 - step(1.0, color.r * color.g * color.b);
}
float blur13(vec2 pos){
  float alpha = 0.0;
  vec2 off1 = vec2(1.411764705882353);
  vec2 off2 = vec2(3.2941176470588234);
  vec2 off3 = vec2(5.176470588235294);
  vec2 off4 = vec2(-1.411764705882353,1.411764705882353);
  vec2 off5 = vec2(-3.2941176470588234,3.2941176470588234);
  vec2 off6 = vec2(-5.176470588235294,5.176470588235294);
  alpha += getA(pos) * 0.1964825501511404;
  alpha += getA(pos + (off1 / size)) * 0.1484534823364172;
  alpha += getA(pos - (off1 / size)) * 0.1484534823364172;
  alpha += getA(pos + (off2 / size)) * 0.04723519892522366;
  alpha += getA(pos - (off2 / size)) * 0.04723519892522366;
  alpha += getA(pos + (off3 / size)) * 0.00519068120057402;
  alpha += getA(pos - (off3 / size)) * 0.00519068120057402;
  alpha += getA(pos + (off4 / size)) * 0.1484534823364172;
  alpha += getA(pos - (off4 / size)) * 0.1484534823364172;
  alpha += getA(pos + (off5 / size)) * 0.04723519892522366;
  alpha += getA(pos - (off5 / size)) * 0.04723519892522366;
  alpha += getA(pos + (off6 / size)) * 0.00519068120057402;
  alpha += getA(pos - (off6 / size)) * 0.00519068120057402;
  return alpha;
}
void main () {
  vec4 o = lightColor;
  o.a = o.a*blur13(v_uv0);
  ALPHA_TEST(o);
  gl_FragColor = o;
}

