glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Properties{
  vec4 addColor;
  vec4 eyeColor;
};
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  bool isEye = false;
  bool isEyebrow = false;
  bool isEyeLight = false;
  #if USE_TEXTURE
  vec4 t = texture(texture, v_uv0);
  if(t.r==1.0&&t.g==0.0&&t.b==0.0){
    isEyebrow = true;
  }else if(t.r==0.0&&t.g==0.0&&t.b==1.0){
    isEye = true;
  }else if(t.r==0.0&&t.g==1.0&&t.b==0.0){
    isEyeLight = true;
  }
  o *= t;
    #if CC_USE_ALPHA_ATLAS_TEXTURE
    o.a *= texture2D(texture, v_uv0 + vec2(0, 0.5)).r;
    #endif
  #endif
  if(isEyebrow){
    o.rgb = eyeColor.rgb*0.6;
  }else if(isEye){
    o.rgb = eyeColor.rgb*0.4;
  }else if(isEyeLight){
    o.rgb = eyeColor.rgb;
  }
  o *= v_color;
  ALPHA_TEST(o);
  o.rgb = o.rgb + addColor.rgb;
  gl_FragColor = o;
}

