glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform OutLine{
  vec4 outlineColor;
  float outlineSize;
  float textureSizeWidth;
  float textureSizeHeight;
  float openOutline;
};
int getIsStrokeWithAngel(float angel)
{
  int stroke = 0;
  vec2 textureSize = vec2(textureSizeWidth,textureSizeHeight);
  float rad = angel * 0.01745329252;
  vec2 unit = 1.0 / textureSize.xy;
  vec2 offset = vec2(outlineSize * cos(rad) * unit.x, outlineSize * sin(rad) * unit.y);
  vec2 v = v_uv0 + offset;
  float a = texture2D(texture, v).a;
  if (a < 0.5 )
  {
      stroke = 1;
  }
  return stroke;
}
void main () {
  vec4 curr_color = texture2D(texture, v_uv0);
  if (curr_color.a < 0.5)
  {
      gl_FragColor = v_color*curr_color;
      return;
  }
  if (openOutline < 0.9)
  {
      gl_FragColor = v_color*curr_color;
      return;
  }
  if(v_uv0.x == 0.0||v_uv0.x==1.0||v_uv0.y==0.0||v_uv0.y==1.0)
  {
    curr_color = outlineColor;
    gl_FragColor = v_color*curr_color;
    return;
  }
  int strokeCount = 0;
  strokeCount += getIsStrokeWithAngel(0.0);
  strokeCount += getIsStrokeWithAngel(30.0);
  strokeCount += getIsStrokeWithAngel(60.0);
  strokeCount += getIsStrokeWithAngel(90.0);
  strokeCount += getIsStrokeWithAngel(120.0);
  strokeCount += getIsStrokeWithAngel(150.0);
  strokeCount += getIsStrokeWithAngel(180.0);
  strokeCount += getIsStrokeWithAngel(210.0);
  strokeCount += getIsStrokeWithAngel(240.0);
  strokeCount += getIsStrokeWithAngel(270.0);
  strokeCount += getIsStrokeWithAngel(300.0);
  strokeCount += getIsStrokeWithAngel(330.0);
  if (strokeCount > 0)
  {
      curr_color = outlineColor;
  }
  gl_FragColor = v_color * curr_color;
}

